import {BrowserRouter, Switch, Route} from 'react-router-dom'
import {HomeView} from '../pages/HomeView'
import {LoginView} from '../pages/LoginView'
import {brandsView} from '../pages/navtabviews/brands/BrandsView'
import {newsView} from '../pages/navtabviews/news/NewsView'
import {shopView} from '../pages/navtabviews/shop/ShopView'
import {accesoriesView} from '../pages/navtabviews/accesories/AccesoriesView'

import RoutingPath from './RoutingPath'

import {UserContext} from '../shared/provider/UserProvider'
import {useEffect, useContext} from 'react'

export const Routes = ({children}) => {
    const [, setAuthUser] = useContext(UserContext)
    useEffect(()=>{
        if (localStorage.getItem('user')){setAuthUser({ username: localStorage.getItem('user')})
    }

    })
    return(
        <BrowserRouter>
        {children}
            <Switch>
                <Route exact path={RoutingPath.home} component={HomeView}/>
                <Route exact path={RoutingPath.login} component={LoginView}/>
                <Route exact path={RoutingPath.accessories} component={accesoriesView}/>
                <Route exact path={RoutingPath.news} component={newsView}/>
                <Route exact path={RoutingPath.brands} component={brandsView}/>
                <Route exact path={RoutingPath.shop} component={shopView}/>
                <Route component={HomeView}/>
            </Switch>
        </BrowserRouter>

    )
}
