import {BrowserRouter, Switch, Route} from 'react-router-dom'
import {HomeView} from '../pages/HomeView'
import {accesoriesView} from '../pages/navigationtabviews/accesoriesView'
import RoutingPath from './RoutingPath'
import {LoginView} from '../pages/LoginView'
import {UserContext} from '../shared/provider/UserProvider'
import {useEffect, useContext} from 'react'

export const Routes = ({children}) =>{
    const [, setAuthUser] = useContext(UserContext)

    useEffect(()=>{
        if (localStorage.getItem('user')){setAuthUser({ username: localStorage.getItem('user')})
    }


    })
    return(
        <BrowserRouter>
        {children}
            <Switch>
                <Route exact path={RoutingPath.homeView} component={HomeView}/>
                <Route exact path={RoutingPath.loginView} component={LoginView}/>
                <Route exact path={RoutingPath.accesoriesView} component={accesoriesView}/>
                <Route exact path={RoutingPath.newsView} component={NewsView}/>
                <Route exact path={RoutingPath.brandsView} component={BrandsView}/>
                <Route exact path={RoutingPath.shopView} component={ShopView}/>
                <Route component={HomeView}/>
            </Switch>
        </BrowserRouter>

    )
}
