import React from 'react'
import {useState, useContext} from 'react'
import {useHistory} from 'react-router-dom'
import RoutingPath from '../routes/RoutingPath'
import {UserContext} from '../shared/provider/UserProvider'

export const LoginView = () => {
    const history = useHistory()
    const[loginDetails, setLoginDetails] = useState({username:'',password:''})
    const [,setAuthUser] = useContext(UserContext)
    const logIn  = () =>{
        localStorage.setItem('user',loginDetails.username)
        localStorage.setItem('passw',loginDetails.password)
        setAuthUser(loginDetails)
        history.push(RoutingPath.homeView)

    }
    return (
        <div align="center">
            <h1>{loginDetails.username}</h1>
           <form>
            username: <input onChange={event => setLoginDetails({ ...loginDetails, username:event.target.value})} /> <br/><br/>

            password: <input onChange={event => setLoginDetails({ ...loginDetails, password:event.target.value})} /> <br/><br/>
            <button onClick={() => logIn()}>Logga in</button>
            </form>
        </div>
    )
}
